import { useEffect, useState, useCallback} from 'react';
import {useParams} from 'react-router-dom';
import StudentList from '../components/StudentList';

function Students() {
  const [students, setStudents] = useState([]);
  const {branchId,query} = useParams();
  console.log("branch",branchId," query",query);

    const fetchStudents=useCallback(async()=>{
        try{

              const response = await fetch(`http://localhost:5000/students/branch/${branchId}`);
              if (!response.ok) {
                throw new Error('Fetching students failed')
              }
              const {students} = await response.json();
              console.log(students);
              setStudents(students);
          }catch(error){
            console.log(error.message);
           }
        },[branchId]);

    useEffect(() => {
     if(branchId)
        fetchStudents();
  },[fetchStudents]);

    useEffect(() => {
       if (items && query) {
           const keywords = query.split(/\s+/);
           const filteredItems = items.filter(item =>
               keywords.every(keyword => item?.desc?.toLowerCase().includes(keyword.toLowerCase()))
           );
           setSearchItems(filteredItems);
       }
   }, [items, query]);


    return (
    <>{students && <StudentList students={students} branchId={branchId} updateStudentList={fetchStudents}/>}</>);
  };

    export default Students;
