import { NavLink } from 'react-router-dom';
import { useState, useEffect } from 'react';
import  classes from './HomePage.module.css';
import Branches from './Branches';
import backgroundImage from '../assets/logo.png'; // Import the image in JS

function HomePage() {
    const [branches, setBranches] = useState([]); // State to hold branch names

    useEffect(() => {
        const fetchBranches = async () => {
      try {
        const response = await fetch('http://localhost:5000/branches'); // Adjust your endpoint
        const data = await response.json();
        console.log(data.branches);

        setBranches(data.branches); // Assuming data is an array of branch names
      } catch (error) {
        console.error('Error fetching branches:', error);
      }
    };
       // fetchBranches();
    }, []);

  return (<>
<div
      style={{
        backgroundImage: `url(${backgroundImage})`,
        backgroundSize: 'cover',
        backgroundRepeat: 'no-repeat',
        height: '100vw',
        width: '10vw',
        position: 'relative', // Ensure background stays behind content
        zIndex: 0, // Keep behind content
      }}
    >


      <div style={{ position: 'relative', zIndex: 1, backgroundColor: 'rgba(255, 255, 255, 0.8)', // Adjust opacity as needed
 }}>
        <Branches/>
      </div>

   </div>
  </>);
}

export default HomePage;
