import {useEffect, useState, useContext, useRef} from 'react';
import { useNavigate, NavLink } from 'react-router-dom';
import './navigation.css';
import Drawer from './Drawer';
import '@fortawesome/fontawesome-free/css/all.min.css';

function Navigation() {
    const [isDrawerOpen, setDrawerOpen] = useState(false);
    const inputRef = useRef();
    const navigate=useNavigate();

const toggleDrawer = () => {
    setDrawerOpen((prevState) => !prevState);
  };

  const handleSearch=(e)=>{
      e.preventDefault();
      const query = inputRef.current.value; // Get the value from the input field
      console.log(query);
      navigate(`/students?query=${query}`);
    }

     const handleKeyPress = (e) => {
        if (e.key === 'Enter') {
            handleSearch(e);
        }
    };

  return (
    <div style={{width:'100%'}}>
      <header>
      <ul style={{ listStyleType: 'none', padding: 0 ,display:'flex', padding: 0, justifyContent: 'space-between', alignItems: 'center',}}>

        <li>
        <form onSubmit={handleSearch} style={{ display: 'flex', alignItems: 'center' }}>
          <button type="submit" style={{ background: 'none', border: 'none', cursor: 'pointer' }}>
            <i style={{ color: '#e44da5', marginRight: '8px' }} className="fa-solid fa-magnifying-glass"></i>
          </button>
          <input
             ref={inputRef}
            type="text"
            placeholder="Search student..."
            //onKeyPress={handleKeyPress}
            style={{
              height: '30px',
              borderRadius: '4px',
              border: '1px solid #ccc',
              padding: '0 8px',
              fontSize: '14px',
              outline: 'none'
            }}
          />
        </form>
      </li>

      <li><NavLink to="/homepage" ><h1 className="logo">Swar Sanskaar </h1></NavLink></li>
      <li>
      <div
          style={{
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            width: '40px',
            height: '40px',
            backgroundColor: 'white', // or any background color you prefer
            borderRadius: '50%',
            fontSize: '20px', // adjust size as needed
            color: 'black', // or any text color you prefer
            border: 'none'
          }}
          onClick={toggleDrawer}>
          <i class="fa-solid fa-chevron-down"></i>
        </div>
        </li>
      </ul>
    <Drawer isOpen={isDrawerOpen} onClose={toggleDrawer} />

      </header>
    </div>
  );
}

export default Navigation;
