import {useEffect, useState, useContext, useRef} from 'react';
import { useNavigate, NavLink } from 'react-router-dom';
import './navigation.css';
import Drawer from './Drawer';
import '@fortawesome/fontawesome-free/css/all.min.css';

function Navigation() {
    const [isDrawerOpen, setDrawerOpen] = useState(false);
    const inputRef1 = useRef();
    const inputRef2 = useRef();

    const navigate=useNavigate();

const toggleDrawer = () => {
    setDrawerOpen((prevState) => !prevState);
  };

  const handleSearch1=(e)=>{
      e.preventDefault();
      const query1 = inputRef1.current.value; // Get the value from the input field
      const query2 = inputRef2.current.value; // Get the value from the input field

      console.log(query1, query2);
      navigate(`/students?query=${query1}`);
    }

     const handleKeyPress = (e) => {
        if (e.key === 'Enter') {
            handleSearch(e);
        }
    };

  return (
    <div style={{width:'100%'}}>
      <header>
      <ul style={{ listStyleType: 'none', padding: 0 ,display:'flex', padding: 0, justifyContent: 'space-between', alignItems: 'center',}}>

        <li>
            <form onSubmit={handleSearch1} style={{ display: 'flex', alignItems: 'center' }}>
              <button type="submit" style={{ background: 'none', border: 'none', cursor: 'pointer' }}>
                <i style={{ color: '#e44da5', marginRight: '8px' }} className="fa-solid fa-magnifying-glass"></i>
              </button>
              <input
                 ref={inputRef1}
                type="text"
                placeholder="Search student..."
                //onKeyPress={handleKeyPress}
                style={{
                  height: '30px',
                  borderRadius: '4px',
                  border: '1px solid #ccc',
                  padding: '0 8px',
                  fontSize: '14px',
                  outline: 'none'
                }}
              />
          </form>

          <form onSubmit={handleSearch2} style={{ display: 'flex', alignItems: 'center' }}>
            <button type="submit" style={{ background: 'none', border: 'none', cursor: 'pointer' }}>
              <i style={{ color: '#e44da5', marginRight: '8px' }} className="fa-solid fa-magnifying-glass"></i>
            </button>
            <input
               ref={inputRef2}
              type="text"
              placeholder="Search student..."
              //onKeyPress={handleKeyPress}
              style={{
                height: '30px',
                borderRadius: '4px',
                border: '1px solid #ccc',
                padding: '0 8px',
                fontSize: '14px',
                outline: 'none'
              }}
            />
        </form>
      </li>

      <li><NavLink  style={{textDecoration:'none'}} to="/" ><h1 className="logo">Swar Sanskaar </h1></NavLink></li>
      <li>
      <div
          style={{
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            width: '40px',
            height: '40px',
            backgroundColor: 'white', // or any background color you prefer
            borderRadius: '50%',
            fontSize: '20px', // adjust size as needed
            color: 'black', // or any text color you prefer
            border: 'none'
          }}
          onClick={toggleDrawer}>
          <i class="fa-solid fa-chevron-down"></i>
        </div>
        </li>
      </ul>
    <Drawer isOpen={isDrawerOpen} onClose={toggleDrawer} />

      </header>
    </div>
  );
}

export default Navigation;
