import {
  Form,
  Link,
  useSearchParams,
  useActionData,
  useNavigate
} from 'react-router-dom';
import {useLocation, useParams} from 'react-router-dom';
import { useForm, setValue } from 'react-hook-form';
import classes from './RegistrationForm.module.css';
import {useState, useEffect} from 'react';

const FeesPaymentForm=()=>{
    const [branches, setBranches] = useState([]); // State to hold branch names
    const [selectedBranch, setSelectedBranch] = useState(''); // State to store selected branchId
    const [students, setStudents] = useState([]); // State to hold student names
    const [selectedStudent, setSelectedStudent] = useState(''); // State to store selected studentId
    const [studentName,setStudentName]=useState('');    //state to hold student name in case of parameter studentId
    const [branchName, setBranchName]=useState('');     //state to hold branch name in case of parameter studentId

    const [formData, setFormData] = useState({
          name: '',
          email: '',
          phone: '',
          address: [],
          monthly_fees: ''
        });

const {
    register,
    handleSubmit,
    formState: { errors, isSubmitting },
    reset
  } = useForm();

    const { studentId } = useParams();
    const location = useLocation();
    const navigate= useNavigate();

    useEffect(() => {
        const fetchBranches = async () => {
      try {
        const response = await fetch('http://localhost:5000/branches'); // Adjust your endpoint
        const data = await response.json();
        console.log(data.branches);
        setBranches(data.branches); // Assuming data is an array of branch names
      } catch (error) {
        console.error('Error fetching branches:', error);
      }
    };

    const fetchStudents=async()=>{
      try{
           const response=await fetch(`http://localhost:5000/students`,{
                 method:'GET',
                 headers:{
                     'Content-Type':'application/json',
                     }
                 });

           const {students}=await response.json();
           console.log(students);
           setStudents(students);
       }
       catch(error){
           console.log(error);
       }
      };

      const fetchStudent=async()=>{
       if(studentId){
            try{
                 const response=await fetch(`http://localhost:5000/students/${studentId}`);
                 const {student}=await response.json();
                 console.log(student);
                 if(student){
                  setFormData({
                            name: student.name,
                            email: student.email,
                            phone: student.phone,
                            address: student.address,
                            monthly_fees: student.monthly_fees
                          });
                  }
                 if (student?.branch_id) {
                    const branchResp=await fetch(`http://localhost:5000/branches/${student.branch_id}`);
                    const {branch}=await branchResp.json();
                    setBranchName(branch?.branch_name);
                    reset({branch:branch.branch_name});
                 }
             }
             catch(error){
                 console.log(error);
             }
         }
       };

      if (studentId)
         fetchStudent();
      else{
          fetchBranches();
          fetchStudents();
      }
    }, []);



  const submitHandler =async (data) => {
    data.branch_id = selectedBranch;
    console.log(data);
    let response;
      response= await fetch(`http://localhost:5000/students/${studentId}`, {
      method: 'PUT',
      headers: {'Content-Type': 'application/json'},
      body: JSON.stringify(data)
     });

      console.log(response);
      navigate('/students');
  };


   const resetHandler=()=>{
        console.log("in reset");
window.history.back();   };


    const handleBranchChange = (event) => {
         setSelectedBranch(event.target.value); // Set selected branchId
    };

    const handleStudentChange = (event) => {
             setSelectedStudent(event.target.value); // Set selected studentId
        };

   return (
     <div className={classes.container}>
     <form onSubmit={handleSubmit(submitHandler)} className={classes.form}>
        <>
    {studentId ? (<>
         <div className={classes.control}>
           <label htmlFor="name">Student Name</label>
           <p>{formData.name}</p>
         </div>

        <div className={classes.control}>
         <label htmlFor="email">Email</label>
         <p>{formData.email}</p>
       </div>


         <div className={classes.control}>
            <label htmlFor="phone">Phone</label>
            <p>{formData.phone}</p>
          </div>


          <div className={classes.control}>
            <label style={{minWidth:'240px'}} htmlFor="address">Address</label>
            <p>{formData.address}</p>
          </div>

          <div className={classes.control}>
           <label htmlFor="name">Student Name</label>
           <input type="text" id="name" name="name"
             {...register("name", { required: "Student name is required." })}  />
          {errors.name && <p className="errorMsg">{errors.name.message}</p>}
         </div>

         <div className={classes.control}>
          <label htmlFor="branch">Branch</label>
            <input type="text" id="branch" name="branch"
             {...register("branch", { required: "Branch name is required." })}  />
          {errors.branch && <p className="errorMsg">{errors.branch.message}</p>}
        </div>

            <div className={classes.control}>
                <label htmlFor="monthly_fees">Monthly Fees</label>
                <input
                  id="monthly_fees"
                  type="text"
                  name="monthly_fees"
                   {...register("monthly_fees", { required: "PLease enter the fees for this student"})}
                />
                {errors.monthly_fees && (
                     <p className="errorMsg">{errors.monthly_fees.message}</p>)}
              </div>
              </>
      ):(
        <>
        <div className={classes.control}>
          <label htmlFor="branch">Branch</label>
          <select
            id="branch"
            name="branch" value={selectedBranch} onChange={handleBranchChange}>

            <option value="">Select a Branch</option>
            {branches && branches.map((branch) => (
              <option key={branch._id} value={branch._id}>
                {branch.branch_name}
              </option>
            ))}
          </select>
          {errors.branch && <p className="errorMsg">{errors.branch.message}</p>}
        </div>

        <div className={classes.control}>
          <label htmlFor="student">Student</label>
          <select
            id="student"
            name="student" value={selectedStudent} onChange={handleStudentChange}
          >
            <option value="">Select a Student</option>
            {students && students.map((student) => (
              <option key={student._id} value={student.name}>
                {student.name}
              </option>
            ))}
          </select>
          {errors.branch && <p className="errorMsg">{errors.branch.message}</p>}
        </div>
        </>
      )}
      </>

       <div style={{marginTop:'20px'}}>
         <button className={classes.button} type="reset" onClick={resetHandler}>
           Cancel
         </button>
         <button className={classes.button} type="submit" >
           {isSubmitting? 'Submitting': 'Proceed'}
         </button>
        </div>


     </form>
    </div>

   )

}
export default FeesPaymentForm
