import { useEffect, useState, useCallback} from 'react';
import {useParams, useSearchParams} from 'react-router-dom';
import StudentList from '../components/StudentList';

function Students() {
    const [students, setStudents] = useState([]);
    const [searchItems,setSearchItems] =useState([]);
    const [searchParams] = useSearchParams();
    const query1 = searchParams.get('query1');
    const query2 = searchParams.get('query2');
    const {branchId} = useParams();

    const fetchStudents=useCallback(async()=>{
        try{

            const response = await fetch(`http://localhost:5000/students`);
            const {students} = await response.json();
            console.log(students);
            setStudents(students);

            if(branchId || query2)
                setStudents(students.filter(student=>student.branch_id===branchId));
            else if(query1){
                const keywords = query1.split(/\s+/);
                const filteredItems = students.filter(item =>
                keywords.every(keyword => item?.name?.toLowerCase().includes(keyword.toLowerCase()))
                );
                console.log(query1, query2, filteredItems);
                setStudents(filteredItems);
            }
          }catch(error){
            console.log(error.message);
           }
        },[branchId, query1, query2]);

    useEffect(() => {
        console.log("in ue1");
        fetchStudents();
  },[branchId, query1, query2]);

