import { useEffect, useState, useCallback} from 'react';
import {useParams} from 'react-router-dom';
import StudentList from '../components/StudentList';

function Students() {
  const [students, setStudents] = useState([]);
    const [searchItems,setSearchItems] =useState([]);

  const {branchId,query} = useParams();
  //console.log("branch",branchId," query",query);

    const fetchStudents=useCallback(async()=>{
        try{

            let response;
            if(query )
            if(branchId)
               response = await fetch(`http://localhost:5000/students/branch/${branchId}`);
            else
               response = await fetch(`http://localhost:5000/students`);

              if (!response.ok) {
                throw new Error('Fetching students failed')
              }
              const {students} = await response.json();
              console.log(students);
              setStudents(students);
          }catch(error){
            console.log(error.message);
           }
        },[branchId]);

    useEffect(() => {
     if(branchId)
        fetchStudents();
  },[fetchStudents]);

    useEffect(() => {
       if (students && query) {
           const keywords = query.split(/\s+/);
           const filteredItems = students.filter(item =>
               keywords.every(keyword => item?.name?.toLowerCase().includes(keyword.toLowerCase()))
           );
           setStudents(filteredItems);
       }
   }, [ query]);


    return (
    <>{students && <StudentList students={students} branchId={branchId} updateStudentList={fetchStudents}/>}</>);
  };

    export default Students;
