    navigate(`/students/new/${branchId}`);
   }

const changeStatusHandler = async (studentId, currentStatus) => {
    const newStatus = !currentStatus; // Toggle the boolean status

    try {
      const response = await fetch(`http://localhost:5000/students/${studentId}/status`, {
        method: 'PATCH',
        headers: {
          'Content-Type': 'application/json',
        },

        body: JSON.stringify({ active: newStatus }),
      });

      if (!response.ok) {
        throw new Error('Failed to update student status');
      }

      const resData = await response.json();
      console.log('Status updated:', resData);

      // Update student list after status change
      updateStudentList();

    } catch (error) {
      console.error(error.message);
    }
  };

  return (<>
<div>
    {students.length===0 && <h2>There are no Students enrolled currently in this Branch</h2>}
</div>

<ul className={classes.gallery}>
  { students.map((student) => (<>

     <div key={student._id} className={`${classes.responsive} ${classes.item}`}>
     <div style={{display:'flex'}}>
     <li key={student._id} >
        <NavLink  style={{textDecoration:'none'}} to={{pathname:`/student/edit/${student._id}` }}  className={({isActive})=>isActive?classes.linkActive : classes.link}end>
            <div className={classes.desc}>{student.name}</div>
        </NavLink>

      <div className={classes.trashIcon}>
        <button
          onClick={() => changeStatusHandler(student._id, student.active)}
          className={classes.link}
          style={{ border: 'none', background: 'none', color: 'inherit', cursor: 'pointer' }}>
          {student.active ? 'Set as Inactive' : 'Set as Active'}
        </button>
      </div>
     </li>
     </div>
     </div>

 </>))}
</ul>
    <div style={{textAlign:'center', marginTop:'10px'}}><NavLink style={{textDecoration:'none'}} to={{pathname:`/student/new`}}>Add Student</NavLink></div>
</>
);
}
export default StudentList;
